import {Component, effect, inject, signal} from '@angular/core';
import {NzIconDirective} from 'ng-zorro-antd/icon';
import {NewOrderBackendService} from '../new-order-backend.service';
import {NzImageDirective} from 'ng-zorro-antd/image';
import {NzInputNumberComponent} from 'ng-zorro-antd/input-number';
import {FormsModule} from '@angular/forms';
import {toObservable} from '@angular/core/rxjs-interop';
import {PostOrder, PostOrderItem} from '../new-order.interface';

@Component({
  selector: 'app-cart',
  standalone: true,
  imports: [
    NzIconDirective,
    NzImageDirective,
    NzInputNumberComponent,
    FormsModule
  ],
  templateUrl: './cart.component.html',
  styleUrl: './cart.component.css'
})
export class CartComponent {
  backendService = inject(NewOrderBackendService);
  fallbackImage=
    'data:image/png;base64,';
  subtotal = signal(0);

  constructor() {
    toObservable(this.backendService.cartFood).subscribe(
      (value) => {
        this.calculateTotal();
      }
    )
  }

  getFoodImage(image: string) {
    return 'https://restaurantapi.bssoln.com/images/food/' + "/" + image;
  }

  removeFromCart(Foodid: number) {
    let items = this.backendService.cartFood().filter(
      (item) => {
        if (item.food.id !== Foodid) {
          return item;
        }
        return;
      }
    )
    this.backendService.cartFood.set(items);
  }

  calculateTotal() {
    let val = 0;
    this.backendService.cartFood().forEach((item) => {
      val += item.amount * item.quantity;
    });
    this.subtotal.set(val);
  }

  valueChange() {
    this.backendService.cartFood.set([...this.backendService.cartFood()]);
  }

  confirmOrder() {
    let items: PostOrderItem[] = [];
    this.backendService.cartFood().forEach((item) => {
      items.push({
        foodId: item.food.id,
        foodPackageId: null,
        quantity: item.quantity,
        unitPrice: item.amount,
        totalPrice: item.quantity*item.amount,
      })
    })
    let POST_DATA: PostOrder = {
      "tableId": Number(this.backendService.selectedTableId()),
      "orderNumber": new Date().toLocaleString(),
      "amount": Number(this.subtotal()),
      "phoneNumber": null,
      "items": items,
    }
    this.backendService.createOrder(POST_DATA);
  }


}
